#include <stdlib.h>
#include <stdio.h>
#include "String.h"

using namespace vs;

String::String()
{
}

String::String(int volume, const char *src)
{
    //分配volume大小的容量
    this->p = (char *)malloc(sizeof(char)*volume);
    this->length = 0;
    char tmp = *src;
    char *pTmp = p;
    while(tmp != '\0')
    {
        *pTmp = tmp;
        src++;
        pTmp++;
        tmp = *src;

        this->length++;
    }
    pTmp = '\0';
}

void String::show()
{
    printf("%s\n", this->p);
}

/**
 * 获得字符串的长度
 */
int String::getLength()
{
    return this->length;
}

void String::append(const char *src)
{

}

String::~String()
{
    free(this->p);
}
