#ifndef VS_STRING_H
#define VS_STRING_H

namespace vs {

class String {

    public:
        String(int volume, const char *src, int length);
        String(int volume, const char *src);
        ~String();

        void show();
        int getLength();
        int getVolume();
        void append(const char *src);
        void append(const char *src, int len);
        void append(String *src);
        char* pointer(int offset);
        String* split(char sep);

    private:
        int length; //当前长度
        int volume; //当前的最大容量
        char *p;    //初始的地址
};

}

#endif