#ifndef VS_OBJECTARRAY_H
#define VS_OBJECTARRAY_H

namespace vs {

template<typename Type> // 模板声明
class VsObjectArray {

    public:
        VsObjectArray();
        ~VsObjectArray();

        void put(Type *p);

    private:
        Type **p;    //类型数组
        int volume; //现有的容量
        int length; //现有元素的个数
};

template<typename Type> // 模板声明
VsObjectArray<Type>::VsObjectArray()
{
    this->volume = 10;
    //指针数组
    this->p = (Type **)malloc(sizeof(Type *)*this->volume);
    this->length = 0;
    printf("%d\n", 2);
}

template<typename Type> // 模板声明
VsObjectArray<Type>::~VsObjectArray()
{
    for(int i = 0; i < this->length; i++)
    {
        delete *(this->p + i);
    }
    printf("destroy------------\n");
    free(this->p);
}

template<typename Type>
void VsObjectArray<Type>::put(Type *pObj)
{
    *(this->p + this->length) = pObj;
    this->length++;
}

}

#endif