#include <stdlib.h>
#include "StringUtil.h"

StringUtil::StringUtil()
{
}

StringUtil::~StringUtil()
{
}

/**
 * 字符串复制函数
 */
void StringUtil::Copy(const char *src, char *dest)
{
    char tmp = *src;
    while(tmp != '\0')
    {
        *dest = tmp;
        src++;
        dest++;
        tmp = *src;
    }
}

/**
 * 获得字符串的长度
 */
int StringUtil::Length(const char *src)
{
    int len = 0;
    while(*src != '\0')
    {
        src++;
        len++;
    }
    return len;
}